/** @jsx React.DOM */
jest.dontMock('../TestBar.js');

describe('test', function() {
  it('is an example testmenu rendering', function() {
    var React = require('react/addons');
    var TestBar = require('../TestBar.js');
    var TestUtils = React.addons.TestUtils;

    //mock data
    var data = [
      {name: "Home"},
      {name: "Messages"},
      {name: "live"}
    ];

    //Render menubar
    var testbar = TestUtils.renderIntoDocument(
      <TestBar data={data}></TestBar>
    );

    //Varify that data object is being generated by component
    var ul = TestUtils.findRenderedDOMComponentWithTag(testbar, 'ul');
    expect(ul.getDOMNode().textContent).toContain("Home");

  });
});




























